SELECT h.h_empno, h.h_salary, h.h_date, l.l_empno, l.l_salary, l.l_date FROM (SELECT e1.emp_no as h_empno, s1.salary as h_salary, e1.hire_date as h_date FROM employees as e1 INNER JOIN salaries as s1 ON s1.emp_no = e1.emp_no WHERE YEAR(e1.birth_date) = 1965 AND YEAR(s1.to_date) = 9999) as h INNER JOIN (SELECT e2.emp_no as l_empno, s2.salary as l_salary, e2.hire_date as l_date FROM employees as e2 INNER JOIN salaries as s2 ON s2.emp_no = e2.emp_no WHERE YEAR(e2.birth_date) = 1965 AND YEAR(s2.to_date) = 9999) as l ON h.h_salary > l.l_salary AND h.h_date > l.l_date ORDER BY h.h_empno, l.l_empno;
