SELECT e1, e2 FROM (SELECT cd.emp_no as e1 FROM dept_emp as cd INNER JOIN employees as e ON cd.emp_no = e.emp_no WHERE cd.dept_no = 'd001' AND YEAR(e.birth_date) = 1955 AND YEAR(cd.to_date) = 9999) as tb1 INNER JOIN (SELECT cd.emp_no as e2 FROM dept_emp as cd INNER JOIN employees as e ON cd.emp_no = e.emp_no WHERE cd.dept_no = 'd001' AND YEAR(e.birth_date) = 1955 AND YEAR(cd.to_date) = 9999) as tb2 WHERE e1 < e2 ORDER BY e1,e2;
